import React, { useEffect, useState } from 'react'
import {Table,Row,Col,Button} from 'react-bootstrap';

const Getapi = () => {
    const [data,setData]=useState([])
    const [name,setNam]=useState("");
    const [clas,setClas]=useState("");
    const [cit,setCit]=useState("");
    useEffect(()=>{
      getApi()
    },[])
  function getApi(){
    fetch("http://localhost:3000/users").then((result)=>{
      result.json().then((resp)=>{
         setData(resp)
         setNam(resp[0].name)
         setClas(resp[0].clas)
         setCit(resp[0].cit)
      })
   })

  }
 function deleteUser(id){
  fetch("http://localhost:3000/users/"+id,{
       method:'DELETE'
  }).then((res)=>{
    res.json().then((resp)=>{
      getApi()
    })
  })
 }  
  return (
    <>
    <h1>Delete/Edit/Get api</h1>
    <Table striped bordered hover>
  <thead>
    <tr>
      <th>ID</th>
      <th>Name</th>
      <th>Qualification</th>
      <th>City</th>
      <th>Operations</th>
    </tr>
  </thead>
  <tbody>
       {
            data.map((item)=>
             <tr>
                 <td>{item.id}</td>
                 <td>{item.name}</td>
                 <td>{item.classt}</td>
                 <td>{item.city}</td>
                 <td><button onClick={()=>deleteUser(item.id)}>Delete</button></td>
             </tr>
            )
        }
   
  </tbody>
</Table>
<div>
  <input type="text" value={name} /><br/><br/>
  <input type="text" value={clas} /><br/><br/>
  <input type="text" value={cit}/><br/><br/>
  <button>updateUser</button>
</div>
    </>
  )
}

export default Getapi
