import React, { useState } from 'react'
import { Table, Row, Col, Button,Form } from 'react-bootstrap';
const Postapi = () => {
const [name,Setname]=useState("");
const [classt,Setclass]=useState("");
const [city,Setcity]=useState("");
function saveUser()
{
   //console.log(name,clas,city);
   let data={name,classt,city};
   fetch("http://localhost:3000/users",{ method:'POST', headers:{'Accept':'application/json', 'content-Type':'application/json' },
     body:JSON.stringify(data)
   }).then((res)=>{
   //console.log("result",res);
   res.json().then((resp)=>{
   // console.log("resp",resp);
   })
   })
}
  return (
    <>
    <Form>
      <tr><h2>You can Create New Recoard here</h2></tr>
      <tr>
        <td><Form.Control type="text" value={name} onChange={(e)=>{Setname(e.target.value)}}  name="name"/> </td>
        <td><Form.Control type="text" value={classt} onChange={(e)=>{Setclass(e.target.value)}} name="class"/> </td>
        <td><Form.Control type="text" value ={city} onChange={(e)=>{Setcity(e.target.value)}} name="city"/></td>
        <td><Button type="button" onClick={saveUser}>Save</Button></td>
      </tr>
    </Form>
    </>
  )
}

export default Postapi
